require(pacman)
p_load(tidytuesdayR, tidyverse, janitor, tidytext, extrafont, slider, here, TTR, wordcloud2, tm, textdata, treemapify, spellbook)
tuesdata &lt;- tidytuesdayR::tt_load('2025-12-30')
christmas_novel_authors &lt;- tuesdata$christmas_novel_authors
christmas_novel_text &lt;- tuesdata$christmas_novel_text
christmas_novels &lt;- tuesdata$christmas_novels
text &lt;- christmas_novel_text |&gt; select(2) |&gt; 
  unnest_tokens(word, text)

word_cleaned &lt;- text |&gt; 
  drop_na() |&gt; 
  anti_join(stop_words, by = "word") |&gt; 
  filter(!word %in% stopwords("en")) |&gt; 
  filter(!word %in% stopwords("de")) 

wordcount &lt;- word_cleaned |&gt; 
  count(word, sort = TRUE)

require(pacman)
p_load(tidytuesdayR, tidyverse, janitor, tidytext, extrafont, slider, here, TTR)
options(scipen = 999)
tuesdata &lt;- tidytuesdayR::tt_load('2025-07-01')
weekly_gas_prices &lt;- tuesdata$weekly_gas_prices

# Extra cpi data 
cpi &lt;- read_csv(here("2025-07-01 - US Weekly Gas Prices/cpi.csv")) |&gt; 
  mutate(year = year(observation_date)) |&gt; 
  rename("cpi" = 2) |&gt; select(-1)
vol_df &lt;- weekly_gas_prices |&gt; 
  filter(grade == "all") |&gt; 
  filter(!formulation %in% c("conventional", "reformulated")) |&gt; 
  mutate(year = year(date)) |&gt; 
  left_join(cpi, by = "year") |&gt; 
  mutate(adjusted_price = price * (144.475 / cpi)) |&gt; 

  mutate(fuel = str_to_title(fuel)) |&gt; 
  arrange(fuel, date) |&gt; 
  drop_na(adjusted_price) |&gt; 

mutate(
  log_return = log(adjusted_price / lag(adjusted_price)),

  vol_12w = slide_dbl(
    log_return, sd,
    .before = 11, .complete = TRUE
  ),

  vol_52w = slide_dbl(
    log_return, sd,
    .before = 51, .complete = TRUE
  ),

  .by = fuel
) |&gt; 

  group_by(fuel) |&gt; 
  mutate(volatility = sd(log_return, na.rm = TRUE)) |&gt; 
  ungroup()

require(pacman)
p_load(tidytuesdayR, tidyverse, janitor, spellbook)
tuesdata &lt;- tidytuesdayR::tt_load('2025-01-21')
exped_tidy &lt;- tuesdata$exped_tidy
peaks_tidy &lt;- tuesdata$peaks_tidy
attempts &lt;- exped_tidy |&gt; 
  left_join(peaks_tidy, by = "PEAKID") |&gt; 
  clean_names() |&gt; 
  mutate(across(starts_with("success"), ~as.character(.))) |&gt; 
  pivot_longer(cols = matches("(route|success)[1-4]"),
               names_to = c(".value", "attempt"),
               names_pattern = "(route|success)([1-4])") |&gt; 
  select(expid, pkname, year, season_factor, route, attempt, success, heightm) |&gt; 
  drop_na(route)

require(pacman)
p_load(tidytuesdayR, tidyverse, janitor, here, spellbook, rnaturalearth, rnaturalearthdata, glue, ggtext, showtext, ggside)
tuesdata &lt;- tidytuesdayR::tt_load('2026-01-20')
apod &lt;- tuesdata$apod
sun_mentions &lt;- apod |&gt; 
mutate(
  sun_mention =
    str_detect(title, regex("(?&lt;![A-Za-z])sun(?![A-Za-z])", ignore_case = TRUE)) |
    str_detect(explanation, regex("(?&lt;![A-Za-z])sun(?![A-Za-z])", ignore_case = TRUE)))

sun_mentions_cleaned &lt;- sun_mentions |&gt; 
  filter(sun_mention == TRUE)

# Compute % of month with sun mentioned
apod_solar &lt;- sun_mentions |&gt;
  mutate(
    month = month(date, label = TRUE, abbr = TRUE),
    year = year(date)) |&gt;
  group_by(year, month) |&gt;
  summarise(
    sun_share = mean(sun_mention, na.rm = TRUE),
    .groups = "drop")

apod_solar_side_x &lt;- sun_mentions |&gt;
  mutate(
    month = month(date, label = TRUE, abbr = TRUE),
    year = year(date)) |&gt;
  group_by(year) |&gt;
  summarise(
    sun_share = sum(sun_mention, na.rm = TRUE),
    .groups = "drop")

apod_solar_side_y &lt;- sun_mentions |&gt;
  mutate(
    month = month(date, label = TRUE, abbr = TRUE),
    year = year(date)) |&gt;
  group_by(month) |&gt;
  summarise(
    sun_share = sum(sun_mention, na.rm = TRUE),
    .groups = "drop")
